import { WebGLRenderer } from 'three/src/renderers/WebGLRenderer';
import { WebGLProgram } from 'three/src/renderers/webgl/WebGLProgram';
import { WebGLCapabilities } from 'three/src/renderers/webgl/WebGLCapabilities';
import { WebGLExtensions } from 'three/src/renderers/webgl/WebGLExtensions';
import { WebGLTextures } from 'three/src/renderers/webgl/WebGLTextures';
import { ShaderMaterial } from 'three/src/materials/ShaderMaterial';

export class WebGLPrograms {
  constructor(renderer: WebGLRenderer, extensions: WebGLExtensions, capabilities: WebGLCapabilities, textures: WebGLTextures);

  programs: WebGLProgram[];

  getParameters(
    material: ShaderMaterial,
    lights: any,
    fog: any,
    nClipPlanes: number,
    object: any
  ): any;
  getProgramCode(material: ShaderMaterial, parameters: any): string;
  acquireProgram(
    material: ShaderMaterial,
    parameters: any,
    code: string
  ): WebGLProgram;
  releaseProgram(program: WebGLProgram): void;
}
